lity/**\n          filters:\n            - StripPrefix=1\n        - id: mes-produce\n          uri: lb://mes-produce\n          predicates:\n            - Path=/produce/**\n          filters:\n            - StripPrefix=1\n        - id: mes-message\n          uri: lb://mes-message\n          predicates:\n            - Path=/message/**\n          filters:\n            - StripPrefix=1\n# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    enabled: false\n    type: math\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/logout\n      - /auth/login\n      - /auth/register\n      - /*/v2/api-docs\n      - /csrf\n      - /technology/technologies/actions/pages/\n      - /technology/technology-changes-histories/actions/pages/\n      - /technology/work-procedure-changes/actions/pages/\n      - /technology/technology-reports/actions/by-technology-status/\n      - /technology/technology-document-control-records/actions/page-no-log/\n      - /quality/quality-inspection-reports/actions/by-categories/\n      - /quality/quality-inspection-reports/actions/by-inspection-types/\n      - /quality/quality-inspection-reports/actions/by-work-procedure-names/\n      - /system/dict/data/type/{dictType}\n      - /quality/un_qualified_product_processes/\n\nlogging:\n  level:\n    root: info\n    #    org.springframework.web: DEBUG\n    com.ruoyi.gateway: info\n    org.apache.ibatis.logging.stdout.StdOutImpl: info\n  file:\n    name: /home/logs/mes-gateway/mes-gateway.log', '0e7569d723736792ef69eab3253df1b0', '2022-04-20 07:08:20', '2022-07-13 07:21:13', 'nacos', '0:0:0:0:0:0:0:1', '', 'suzhou-mes-dev', '网关模块', '', '', 'yaml', '');
