INSERT INTO `config_info` VALUES (78, 'application.yml', 'DEFAULT_GROUP', 'spring:\n  main:\n    allow-circular-references: true\n    allow-bean-definition-overriding: true\n  autoconfigure:\n    exclude: com.alibaba.druid.spring.boot.autoconfigure.DruidDataSourceAutoConfigure\n  mvc:\n    pathmatch:\n      matching-strategy: ant_path_matcher\n  cloud:\n    sentinel:\n      filter:\n        # sentinel 在 springboot 2.6.x 不兼容问题的处理\n        enabled: false\n\n# feign 配置\nfeign:\n  sentinel:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 10000\n        readTimeout: 10000\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n', 'e6a8dafc6fcec97f777836bea3ed5028', '2022-04-22 01:40:07', '2022-04-22 01:40:07', NULL, '0:0:0:0:0:0:0:1', '', 'suzhou-mes-prod', '通用配置', NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (79, 'mes-gateway.yml', 'DEFAULT_GROUP', 'spring:\n  redis:\n    host: localhost\n    port: 6379\n    password: 123456\n  cloud:\n    gateway:\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: mes-auth\n          uri: lb://mes-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - CacheRequestFilter\n            - ValidateCodeFilter\n            - StripPrefix=1\n        # 定时任务\n        - id: mes-job\n          uri: lb://mes-job\n          predicates:\n            - Path=/schedule/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: mes-system\n          uri: lb://mes-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n        # 文件服务\n        - id: mes-file\n          uri: lb://mes-file\n          predicates:\n            - Path=/file/**\n          filters:\n            - StripPrefix=1\n        # 审批服务\n        - id: mes-approve\n          uri: lb://mes-approve\n          predicates:\n            - Path=/mes-approve/**\n          filters:\n            - StripPrefix=1\n        - id: mes-technology\n          uri: lb://mes-technology\n          predicates:\n            - Path=/mes-technology/**\n          filters:\n            - StripPrefix=1\n\n# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    enabled: true\n    type: math\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/logout\n      - /auth/login\n      - /auth/register\n      - /*/v2/api-docs\n      - /csrf\n', '6314ce58160583c9a298d0eed006d722', '2022-04-22 01:40:07', '2022-04-22 01:40:07', NULL, '0:0:0:0:0:0:0:1', '', 'suzhou-mes-prod', '网关模块', NULL, NULL, 'yaml', NULL);
