INSERT INTO `his_config_info` VALUES (284, 503, 'mes-gateway.yml', 'DEFAULT_GROUP', '', 'spring:\n  redis:\n    host: 192.168.8.17\n    port: 6379\n    password: Admin@redis123\n    database: 5\n  cloud:\n    gateway:\n      globalcors:\n        corsConfigurations:\n          \'[/**]\':\n            allowedOriginPatterns: \"*\"\n            allowed-methods: \"*\"\n            allowed-headers: \"*\"\n            allow-credentials: true\n            exposedHeaders: \"Content-Disposition,Content-Type,Cache-Control\"\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: mes-auth\n          uri: lb://mes-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - CacheRequestFilter\n            - ValidateCodeFilter\n            - StripPrefix=1\n        # 系统模块\n        - id: mes-system\n          uri: lb://mes-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n        # 文件服务\n        - id: mes-file\n          uri: lb://mes-file\n          predicates:\n            - Path=/file/**\n          filters:\n            - StripPrefix=1\n        - id: mes-equipment\n          uri: lb://mes-equipment\n          predicates:\n            - Path=/equip/**\n          filters:\n            - StripPrefix=1\n        - id: mes-material\n          uri: lb://mes-material\n          predicates:\n            - Path=/material/**\n          filters:\n            - StripPrefix=1\n        - id: mes-base\n          uri: lb://mes-base\n          predicates:\n            - Path=/base/**\n          filters:\n            - StripPrefix=1\n# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    enabled: false\n    type: math\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/logout\n      - /auth/login\n      - /auth/register\n      - /*/v2/api-docs\n      - /csrf\n      - /base/system/getApkVersion\n      - /equip/bi/**\n\nlogging:\n  level:\n    root: info\n    com.ruoyi.gateway: info\n', '3e2a125c06e95fce9b6b59ecb706d135', '2022-09-29 14:12:05', '2022-09-29 06:12:05', 'nacos', '0:0:0:0:0:0:0:1', 'U', 'zc-mes-dev');
INSERT INTO `his_config_info` VALUES (320, 504, 'mes-material.yml', 'DEFAULT_GROUP', '', '# spring配置\nspring:\n  redis:\n    host: 192.168.8.17\n    port: 6379\n    password: Admin@redis123\n    database: 5\n  datasource:\n    druid:\n      stat-view-servlet:\n        enabled: true\n        loginUsername: admin\n        loginPassword: 123456\n    dynamic:\n      druid:\n        initial-size: 5\n        min-idle: 5\n        maxActive: 20\n        maxWait: 60000\n        timeBetweenEvictionRunsMillis: 60000\n        minEvictableIdleTimeMillis: 300000\n        validationQuery: SELECT 1 FROM DUAL\n        testWhileIdle: true\n        testOnBorrow: false\n        testOnReturn: false\n        poolPreparedStatements: true\n        maxPoolPreparedStatementPerConnectionSize: 20\n        filters: stat,slf4j\n        connectionProperties: druid.stat.mergeSql\\=true;druid.stat.slowSqlMillis\\=5000\n      datasource:\n          # 主库数据源\n          master:\n            driver-class-name: com.mysql.jdbc.Driver\n            url: jdbc:mysql://192.168.8.15:3306/zc_mes_cloud_dev?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&serverTimezone=GMT%2B8\n            username: zcx\n            password: zcx@mysql6789\n          # 从库数据源\n          # slave:\n            # username:\n            # password:\n            # url:\n            # driver-class-name:\n      # seata: true    # 开启seata代理，开启后默认每个数据源都代理，如果某个不需要代理可单独关闭\n\n\nmybatis-plus:\n  type-aliases-package: com.jnf.product.mes.material.mapper\n  configuration:\n    map-underscore-to-camel-case: true #驼峰自动映射\n    lazy-loading-enabled: true\n    multiple-result-sets-enabled: true\n    log-impl: org.apache.ibatis.logging.slf4j.Slf4jImpl  # 日志控制级别\n  global-config:\n    banner: false\n    db-config:\n      id-type: AUTO\n      table-underline: true\n  mapper-locations: classpath*:/mapper/*.xml\n\n\n# swagger配置\nswagger:\n  title: MES审批模块接口文档\n\n\n# seata配置\nseata:\n  # 默认关闭，如需启用spring.datasource.dynamic.seata需要同时开启\n  enabled: false\n  # Seata 应用编号，默认为 ${spring.application.name}\n  application-id: ${spring.application.name}\n  tx-service-group: zc-mes-${spring.config.activate.on-profile}\n  # 关闭自动代理\n  enable-auto-data-source-proxy: false\n  # 服务配置项\n  service:\n    # 虚拟组和分组的映射\n    vgroup-mapping:\n      zc-mes-test: zc-mes-${spring.config.activate.on-profile}\n  config:\n    type: nacos\n    nacos:\n      serverAddr: ${spring.cloud.nacos.discovery.server-addr}\n      group: SEATA_GROUP\n      namespace: zc-mes-${spring.config.activate.on-profile}\n  registry:\n    type: nacos\n    nacos:\n      group: SEATA_GROUP\n      server-addr: ${spring.cloud.nacos.discovery.server-addr}\n      namespace: zc-mes-${spring.config.activate.on-profile}\n  client:\n    undo:\n      log-serialization: kryo\n\nlogging:\n  level:\n    root: info\n    com.jnf.zc.product.mes.material: info\n    com.jnf.zc.product.mes.material.mapper: debug', 'b00c0880598299be4c78d665a7659d6b', '2022-09-29 15:01:05', '2022-09-29 07:01:06', 'nacos', '0:0:0:0:0:0:0:1', 'U', 'zc-mes-dev');
